{
   free(s->array);
}

static void dsm_sorter_Sorter_add_dep(dsm_sorter_Sorter* s, uint32_t src, uint32_t dst)
{
   uint32_t offset = ((src * s->count) + dst);
   s->array[offset] = 1;
}

