   string_buffer_Buf_add(out, ast_callKind_names[ast_FunctionDecl_getCallKind(d)]);
   ast_Decl_printAttrs(&d->parent, out);
   if (d->parent.functionDeclBits.is_type) string_buffer_Buf_add(out, " Type");
