   string_buffer_Buf_clear(buf);
   string_buffer_Buf_add(buf, "plugin deps_generator [all-targets files private]\n");
   string_buffer_Buf_add(buf, "plugin refs_generator [all-targets]\n");
   string_buffer_Buf_newline(buf);
