   ast_Decl* d = ((ast_Decl*)(i));
   ast_Decl_setUsed(d);
   ast_Decl_setChecked(d);
   ast_ImportDecl_setDest(i, b->mod);
   ast_Decl_setType(d, ast_QualType_create(((ast_Type*)(ast_Module_getType(b->mod)))));
