   case '\r':
      fallthrough;
   case '\n':
      c2_tokenizer_Tokenizer_error(t, result, "unterminated %s", stype);
      return 0;
   case '0':
      fallthrough;
   case '1':
      fallthrough;
   case '2':
      fallthrough;
   case '3':
      fallthrough;
   case '4':
      fallthrough;
   case '5':
      fallthrough;
   case '6':
