   case token_Kind_Feat_ifdef:
      fallthrough;
   case token_Kind_Feat_ifndef:
      fallthrough;
   case token_Kind_Feat_elif:
      if (c2_tokenizer_Tokenizer_handle_if(t, result, kind)) return true;

      break;
   case token_Kind_Feat_else:
