            name_vector_NameVector_init(&sub_names, ast_StructTypeDecl_getNumMembers(sub));
            name_vector_NameVector sub_locs;
            name_vector_NameVector_init(&sub_locs, ast_StructTypeDecl_getNumMembers(sub));
